import axios from 'axios';

const URL = 'https://openweathermap.org/data/2.5/weather';
const API_Key = 'a8cf20c27ef87bdbea0cab0909409ecf';

export const fetchWeather = async (query) =>  {
          const {data} = await axios.get(URL, {
          params:{
                q : query,
                units: 'metric',
                APPID: API_Key,
          }
        });
          return data;
}
