import React, { useState } from 'react';
import { fetchWeather }  from './api/fetchWeather';
import './app.css';

const App = ()  => { 
      const [query,setQuery] = useState('');     
      const [weather, setWeather] = useState('');

      const search = async(e) => {
          if(e.key == 'Enter'){
              const data = await fetchWeather(query)
            setWeather(data);
            setQuery('');
          }
      }
  return(
            <div>
              <input  type="text" className="search"  placeholder="Search for the city you are looking for.." value={query}  onChange={(e) => setQuery(e.target.value)} onKeyPress={search}
   />
            </div>
        );
}

export default App;
